function loadSwiperHighlights(){new Swiper(".swiper-container.sky-main__highlights",{slidesPerView:3,spaceBetween:30,slidesPerGroup:1,loop:!0,direction:"horizontal",pagination:{el:".swiper-pagination",clickable:!0},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}})}window.onload=function(){fetch("https://sky-frontend.herokuapp.com/movies").then(e=>e.json()).then(e=>function(e){let i=e.filter(e=>"highlights"===e.type.trim().toLowerCase())[0].items,n=document.getElementById("highlights-movies");console.log(i),n.innerHTML=` \n      ${i.map(e=>`<img src='${e.images[0].url.replace("/1920x1080/","/669x391/")}' class='swiper-slide sky-main__highlights-wrapper-image'/>`).join("")}\n    `,loadSwiperHighlights()}(e))};