function sliderLoadHighlights(){$(".sky-main__highlights.owl-carousel").owlCarousel({center:!0,items:3,loop:!0,margin:15,nav:!0}),$(".sky-main__highlights.owl-carousel .owl-prev").html("<i class='fa fa-arrow-left' aria-hidden='true'></i>"),$(".sky-main__highlights.owl-carousel .owl-next").html("<i class='fa fa-arrow-right' aria-hidden='true'></i>")}function sliderLoadMovies(){$(".sky-main__portrait-movie.owl-carousel").owlCarousel({loop:!1,margin:10,lazyLoad:!0,autoWidth:!0,nav:!0,items:7,dots:!1}),$(".sky-main__portrait-movie.owl-carousel .owl-prev").html("<i class='fa fa-arrow-left' aria-hidden='true'></i>"),$(".sky-main__portrait-movie.owl-carousel .owl-next").html("<i class='fa fa-arrow-right' aria-hidden='true'></i>")}window.onload=function(){fetch("https://sky-frontend.herokuapp.com/movies").then(i=>i.json()).then(i=>{!function(i){let e=i.filter(i=>"highlights"===i.type.trim().toLowerCase())[0].items;document.getElementById("highlights-movies").innerHTML=` \n      ${e.map(i=>`<img src='${i.images[0].url.replace("/1920x1080/","/669x391/")}' class='swiper-slide sky-main__highlights-wrapper-image'/>`).join("")}\n    `,sliderLoadHighlights()}(i),function(i){let e=i.filter(i=>"carousel-portrait"===i.type.trim().toLowerCase())[0].movies,t=e.map(i=>i.categories).map(i=>i.split(",")).flat(1/0);t=t.filter((i,e)=>t.indexOf(i)===e);let a=document.getElementById("portrait-movies");for(let i=0;i<t.length;i++){let l=document.createElement("article");l.classList.add("sky-main__portrait-article");let o=e.filter(e=>e.categories.includes(t[i]));console.log(o),l.innerHTML=`\n        <h2 class='sky-main__portrait-title'>${t[i]}</h2>\n        <div class='sky-main__portrait-movie owl-carousel'>${o.map(i=>`\n          <div class='owl-item__group'>\n            <img src='${i.images[0].url}' alt='${i.title}' title='${i.title}'/>\n            ${i.isBlocked?'<i class="fa fa-lock" aria-hidden="true"></i>':'<i class="fa fa-shopping-cart" aria-hidden="true"></i>'}\n          </div>\n          `).join("")}</div>\n      `,a.appendChild(l)}sliderLoadMovies()}(i)})};