function loadDarkTheme(){document.getElementById("theme-option").addEventListener("click",(function(){document.getElementsByTagName("body")[0].classList.toggle("dark-theme")}))}function sliderLoadHighlights(){$(".sky-main__highlights.owl-carousel").owlCarousel({center:!0,items:3,loop:!0,margin:15,nav:!0}),$(".sky-main__highlights.owl-carousel .owl-prev").html("<i class='fa fa-arrow-left' aria-hidden='true'></i>"),$(".sky-main__highlights.owl-carousel .owl-next").html("<i class='fa fa-arrow-right' aria-hidden='true'></i>")}function sliderLoadMovies(){$(".sky-main__portrait-movie.owl-carousel").owlCarousel({loop:!1,margin:10,lazyLoad:!0,autoWidth:!0,nav:!0,items:7,dots:!1}),$(".sky-main__portrait-movie.owl-carousel .owl-prev").html("<i class='fa fa-arrow-left' aria-hidden='true'></i>"),$(".sky-main__portrait-movie.owl-carousel .owl-next").html("<i class='fa fa-arrow-right' aria-hidden='true'></i>")}window.onload=function(){fetch("https://sky-frontend.herokuapp.com/movies").then(e=>e.json()).then(e=>{!function(e){let i=e.filter(e=>"highlights"===e.type.trim().toLowerCase())[0].items;document.getElementById("highlights-movies").innerHTML=` \n      ${i.map(e=>`<img src='${e.images[0].url.replace("/1920x1080/","/669x391/")}' class='swiper-slide sky-main__highlights-wrapper-image'/>`).join("")}\n    `,sliderLoadHighlights()}(e),function(e){let i=e.filter(e=>"carousel-portrait"===e.type.trim().toLowerCase())[0].movies;console.log(i);let t=i.map(e=>e.categories).map(e=>e.split(",")).flat(1/0);t=t.map(e=>e.trim()),t=t.filter((e,i)=>t.indexOf(e)===i);let a=document.getElementById("portrait-movies");for(let e=0;e<t.length;e++){let l=document.createElement("article");l.classList.add("sky-main__portrait-article");let o=i.filter(i=>i.categories.includes(t[e]));l.innerHTML=`\n        <h2 class='sky-main__portrait-title'>${t[e]}</h2>\n        <div class='sky-main__portrait-movie owl-carousel'>${o.map(e=>`\n          <div class='owl-item__group'>\n            <a href='#'><img src='${e.images[0].url}' alt='${e.title}' title='${e.title}'/></a>\n            ${e.isBlocked?'<i class="fa fa-lock" aria-hidden="true"></i>':'<i class="fa fa-shopping-cart" aria-hidden="true"></i>'}\n          </div>\n          `).join("")}</div>\n      `,a.appendChild(l)}sliderLoadMovies()}(e)}),loadDarkTheme()};